    Pressable,
    {
      bg: '$primary600',
      px: '$6',
      py: '$4',
      _text: {
        color: '$white',
      },
    },
    {
      descendantStyle: ['_text'],
    }
  );
  const StyledButtonText = styled(
  Text,
  {
    color: '$grey800',
    fontWeight: '700',
  },
  { ancestorStyle: ['_text'] }
);
return (
