function front(m) {
  // Is this invertible?
  var m2 = CanvasKit.M44.invert(m);
  if (m2 === null) {
    m2 = CanvasKit.M44.identity();
  }
  // look at the sign of the z-scale of the inverse of m.
  // that's the number in row 2, col 2.
  return m2[10]
}
