TITLE: Implementing Multiple Date Selection in React DayPicker
DESCRIPTION: Example of implementing multi-selection behavior (similar to mode="multiple") using an array of dates in state. Includes functionality to add and remove dates from the selection and a reset button.
SOURCE: https://github.com/gpbl/react-day-picker/blob/main/website/docs/guides/custom-selections.mdx#2025-04-18_snippet_2

LANGUAGE: tsx
CODE:
```
import { useState } from "react";

import { isSameDay } from "date-fns";
import { DayMouseEventHandler, DayPicker } from "react-day-picker";

export function CustomMultiple() {
  const [value, setValue] = useState<Date[]>([]);

  const handleDayClick: DayMouseEventHandler = (day, modifiers) => {
    const newValue = [...value];
    if (modifiers.selected) {
      const index = value.findIndex((d) => isSameDay(day, d));
      newValue.splice(index, 1);
    } else {
      newValue.push(day);
    }
    setValue(newValue);
  };

  const handleResetClick = () => setValue([]);

  let footer = <>Please pick one or more days.</>;

  if (value.length > 0)
    footer = (
      <>
        You selected {value.length} days.{" "}
        <button onClick={handleResetClick}>Reset</button>
      </>
    );

  return (
    <DayPicker
      onDayClick={handleDayClick}
      modifiers={{ selected: value }}
      footer={footer}
    />
  );
}
```

