TITLE: Implementing OpenTelemetry Component Interface Methods
DESCRIPTION: Implements required interface methods for the OpenTelemetry processor including default settings, validation, conversion, and various helper methods for configuration handling.
SOURCE: https://github.com/grafana/alloy/blob/main/docs/developer/add-otel-component.md#2025-04-22_snippet_2

LANGUAGE: go
CODE:
```
// Compile time assertions
var (
    _ processor.Arguments = Arguments{}
    _ syntax.Defaulter = &Arguments{}
    _ syntax.Validator = &Arguments{}
)

// DefaultArguments holds default settings for Arguments.
var DefaultArguments = Arguments{
    AffectLogs: true,
    AffectMetrics: true,
    AffectTraces: true,
}

// SetToDefault implements syntax.Defaulter.
func (args *Arguments) SetToDefault() {
    *args = DefaultArguments
    args.DebugMetrics.SetToDefault()
}

// Validate implements syntax.Validator.
func (args *Arguments) Validate() error {
    if args.Attribute == "" {
        return fmt.Errorf("attribute must not be empty")
    }
    return nil
}

// Convert implements processor.Arguments.
func (args Arguments) Convert() (otelcomponent.Config, error) {
    return &exampleprocessor.Config{
        Attribute: args.Attribute,
        Value: args.Value,
        AffectLogs: args.AffectLogs,
        AffectMetrics: args.AffectMetrics,
        AffectTraces: args.AffectTraces,
    }, nil
}

// Extensions implements processor.Arguments.
func (args Arguments) Extensions() map[otelcomponent.ID]otelcomponent.Component {
    return nil
}

// Exporters implements processor.Arguments.
func (args Arguments) Exporters() map[pipeline.Signal]map[otelcomponent.ID]otelcomponent.Component {
    return nil
}

// NextConsumers implements processor.Arguments.
func (args Arguments) NextConsumers() *otelcol.ConsumerArguments {
    return args.Output
}

// DebugMetricsConfig implements processor.Arguments.
func (args Arguments) DebugMetricsConfig() otelcolCfg.DebugMetricsArguments {
    return args.DebugMetrics
}
```

