TITLE: Implementing Conditional Formatting in React with TypeScript and Handsontable
DESCRIPTION: React TSX implementation of conditional formatting in Handsontable, using custom cell renderers to apply different styles based on conditions within a React component with TypeScript typing.
SOURCE: https://github.com/handsontable/handsontable/blob/develop/docs/content/guides/cell-features/conditional-formatting/conditional-formatting.md#2025-04-10_snippet_4

LANGUAGE: tsx
CODE:
```
import { HotTable } from '@handsontable/react';
import Handsontable from 'handsontable';
import { registerAllModules } from 'handsontable/registry';
import 'handsontable/dist/handsontable.full.min.css';
import { useEffect, useRef } from 'react';
import { GridSettings } from 'handsontable/settings';

registerAllModules();

const ExampleComponent = (): JSX.Element => {
  const hotRef = useRef<HotTable>(null);
  const data = [
    ['Tesla', 2017, 'black', 'black', '$5,000.00', '$123,456.00'],
    ['Nissan', 2018, 'blue', 'blue', '$4,000.00', '$234,567.00'],
    ['Chrysler', 2019, 'yellow', 'black', '$4,000.00', ''],
    ['Volvo', 2020, 'white', 'gray', '$5,000.00', '$768,908.00'],
    ['Jaguar', 2019, 'purple', 'black', '$4,000.00', '$876,543.00'],
    ['Ford', 2020, 'blue', 'blue', '$5,000.00', '-$9,123.00'],
    ['Mercedes', 2019, 'blue', 'gray', '$5,000.00', '$123,456.00'],
  ];

  const isEmptyRow = (instance: Handsontable, row: number): boolean => {
    const rowData = instance.getData()[row];

    return rowData.every(element => element === null || element === '');
  };

  return (
    <HotTable
      data={data}
      rowHeaders={true}
      colHeaders={['Car', 'Year', 'Chassis color', 'Bumper color', 'Price', 'Total']}
      height="auto"
      licenseKey="non-commercial-and-evaluation"
      ref={hotRef}
      cells={(row: number, col: number, prop: string): any => {
        const cellProperties: any = {};
        const visualRowIndex = hotRef.current?.hotInstance.toVisualRow(row);
        const visualColIndex = hotRef.current?.hotInstance.toVisualColumn(col);

        cellProperties.renderer = function(instance: Handsontable, td: HTMLTableCellElement, row: number, col: number, prop: string, value: any, cellProperties: any): void {
          Handsontable.renderers.TextRenderer.apply(this, arguments as any);

          td.style.fontWeight = 'normal';
          td.style.color = 'black';
          td.style.background = 'white';

          if (row === 0) {
            td.className = 'myBold';
            td.style.fontWeight = 'bold';
          }

          if (col === 0 && value === 'Nissan') {
            td.className = 'myItalic';
          }

          if (value === null || value === '') {
            td.className = 'myEmpty';
          }

          if (value === '') {
            td.innerHTML = '<div class="relative"><div class="uil-reload-css waitingForConnection" style="transform: scale(0.3);"><div class=""></div></div></div>'
          }

          if (value < 0) {
            td.className = 'myNegative';
          }

          if (visualRowIndex === 0 && visualColIndex === 0) {
            td.style.fontStyle = 'italic';
          }

          if (visualRowIndex !== null && visualRowIndex === isEmptyRow(instance, visualRowIndex)) {
            td.style.background = '#EEE';
          }
        };

        return cellProperties;
      }}
    />
  );
};

export default ExampleComponent;
```

