TITLE: Advanced Redux Integration with Custom Components in TypeScript
DESCRIPTION: A TypeScript implementation demonstrating advanced Handsontable integration with Redux, featuring custom editor and renderer components that communicate through the Redux state.
SOURCE: https://github.com/handsontable/handsontable/blob/develop/docs/content/guides/getting-started/react-redux/react-redux.md#2025-04-10_snippet_3

LANGUAGE: tsx
CODE:
```
import React from 'react';
import { HotTable } from '@handsontable/react-wrapper';
import { registerAllModules } from 'handsontable/registry';
import 'handsontable/dist/handsontable.full.min.css';
import { createStore, Dispatch, Action } from 'redux';
import { Provider, connect } from 'react-redux';
import { HexColorPicker } from 'react-colorful';
import StarRatingComponent from 'react-star-rating-component';
import Handsontable from 'handsontable';

// register Handsontable's modules
registerAllModules();

interface AppState {
  data: (string | number)[][];
  isEditorOpened: boolean;
  editedCarIndex: number;
}

const initialState: AppState = {
  data: [
    ['BMW', 'blue', 4],
    ['Volvo', 'steelblue', 5],
    ['Toyota', 'yellow', 3],
    ['Mercedes', 'red', 2],
    ['Mitsubishi', 'green', 4]
  ],
  isEditorOpened: false,
  editedCarIndex: 0
};

const TOGGLE_EDITOR = 'TOGGLE_EDITOR';
const SET_EDITED_CAR = 'SET_EDITED_CAR';

interface ToggleEditorAction extends Action {
  type: typeof TOGGLE_EDITOR;
  value: boolean;
}

interface SetEditedCarAction extends Action {
  type: typeof SET_EDITED_CAR;
  value: number;
}

type AppActions = ToggleEditorAction | SetEditedCarAction;

const rootReducer = (state: AppState = initialState, action: AppActions): AppState => {
  switch (action.type) {
    case TOGGLE_EDITOR:
      return Object.assign({}, state, {
        isEditorOpened: action.value
      });
    case SET_EDITED_CAR:
      return Object.assign({}, state, {
        editedCarIndex: action.value
      });

    default:
      return state;
  }
};

const toggleEditor = (value: boolean): ToggleEditorAction => {
  return {
    type: TOGGLE_EDITOR,
    value: value
  };
};

const setEditedCar = (value: number): SetEditedCarAction => {
  return {
    type: SET_EDITED_CAR,
    value: value
  };
};

const store = createStore(rootReducer);

interface StateProps {
  hotData: (string | number)[][];
  isEditorOpened: boolean;
  editedCarIndex: number;
}

interface DispatchProps {
  toggleEditor: (value: boolean) => void;
  setEditedCar: (value: number) => void;
}

type ReduxProps = StateProps & DispatchProps;

interface ColorEditorProps extends ReduxProps {
  row: number;
  value: string;
  onFinishEditing: (value?: string) => void;
}

interface ColorEditorState {
  value: string | null;
}

const mapStateToProps = (state: AppState): StateProps => {
  return {
    hotData: state.data,
    isEditorOpened: state.isEditorOpened,
    editedCarIndex: state.editedCarIndex
  };
};

const mapDispatchToProps = (dispatch: Dispatch<AppActions>): DispatchProps => {
  return {
    toggleEditor: (value: boolean) => dispatch(toggleEditor(value)),
    setEditedCar: (value: number) => dispatch(setEditedCar(value))
  };
};

class ColorEditor extends React.Component<ColorEditorProps, ColorEditorState> {
  constructor(props: ColorEditorProps) {
    super(props);

    this.state = {
      value: null
    };

    this.valueChangeCallback = this.valueChangeCallback.bind(this);
    this.keyDownHandler = this.keyDownHandler.bind(this);
  }

  componentDidMount() {
    this.setValue(this.props.value);

    this.props.toggleEditor(true);
    this.props.setEditedCar(this.props.row);
  }

  componentWillUnmount() {
    this.props.toggleEditor(false);
  }

  setValue(value: string) {
    this.setState({
      value: value
    });
  }

  getValue() {
    return this.state.value;
  }

  valueChangeCallback(value: string) {
    this.setState({
      value
    });

    this.finishEditing();
  }

  finishEditing() {
    if (this.props.onFinishEditing) {
      try {
        this.props.onFinishEditing(this.state.value || undefined);

      } catch (error) {
        console.log('An error has occurred while closing the editor.');
      }
    }
  }

  keyDownHandler(event: React.KeyboardEvent) {
    if (event.key === 'Escape') {
      this.props.onFinishEditing();
    }
  }

  render() {
    const style = {
      width: '100%',
      height: '100%',
      backgroundColor: this.state.value || undefined
    };

    return (
      <div style={style} onKeyDown={this.keyDownHandler}>
        <HexColorPicker
          color={this.state.value || undefined}
          onChange={this.valueChangeCallback}
          style={{ width: '100%', height: '100%' }}
        />
      </div>
    );
  }
}

const ConnectedColorEditor = connect(mapStateToProps, mapDispatchToProps)(ColorEditor);

interface ColorRendererProps extends ReduxProps {
  row: number;
  value: string;
}

class ColorRenderer extends React.Component<ColorRendererProps> {
  render() {
    const style = {
      width: '100%',
      height: '100%',
      backgroundColor: this.props.value,
      color: '#eeeeee'
    };

    const className = this.props.isEditorOpened && this.props.editedCarIndex === this.props.row ? 'car-edited' : '';

    return (
      <div style={style} className={className}>
        {this.props.value}
      </div>
    );
  }
}

const ConnectedColorRenderer = connect(mapStateToProps, mapDispatchToProps)(ColorRenderer);

interface StarRendererProps extends ReduxProps {
  row: number;
  value: number;
}

class StarRenderer extends React.Component<StarRendererProps> {
  render() {
    const className = this.props.isEditorOpened && this.props.editedCarIndex === this.props.row ? 'car-edited' : '';

    return (
      <div className={className}>
        <StarRatingComponent
          name="rating"
          editing={false}
          starCount={5}
          value={this.props.value}
        />
      </div>
    );
  }
}

const ConnectedStarRenderer = connect(mapStateToProps, mapDispatchToProps)(StarRenderer);

const renderColor = (instance: Handsontable, td: HTMLTableCellElement, row: number, col: number, prop: string | number, value: any, cellProperties: Handsontable.CellProperties) => {
  cellProperties.renderer = ConnectedColorRenderer;
  cellProperties.editor = ConnectedColorEditor;

  return td;
};

const renderStar = (instance: Handsontable, td: HTMLTableCellElement, row: number, col: number, prop: string | number, value: any, cellProperties: Handsontable.CellProperties) => {
  cellProperties.renderer = ConnectedStarRenderer;

  return td;
};

class HotTableComponent extends React.Component<ReduxProps> {
  render() {
    const columns = [
      {},
      {
        renderer: renderColor
      },
      {
        renderer: renderStar
      }
    ];

    const colHeaders = ["Car", "Color", "Rating"];

    return (
      <div id="example-container">
        <HotTable
          id="hot"
          data={this.props.hotData}
          colHeaders={colHeaders}
          rowHeaders={true}
          columns={columns}
          width="100%"
          height="300px"
          licenseKey="non-commercial-and-evaluation"
        />
      </div>
    );
  }
}

const HotTableContainer = connect(mapStateToProps, mapDispatchToProps)(HotTableComponent);

const App = () => {
  return (
    <Provider store={store}>
      <HotTableContainer />
    </Provider>
  );
};

export default App;
```

