LANGUAGE: jsx
CODE:
```
import { HotTable } from '@handsontable/react';
import { registerAllModules } from 'handsontable/registry';
import { useRef } from 'react';
import 'handsontable/dist/handsontable.full.min.css';

// register Handsontable's modules
registerAllModules();

export const ExampleComponent = () => {
  const hotRef = useRef(null);

  const data = [
    ['A1', 'B1', 'C1', 'D1'],
    ['A2', 'B2', 'C2', 'D2'],
    ['A3', 'B3', 'C3', 'D3'],
    ['A4', 'B4', 'C4', 'D4'],
  ];

  const exportBlob = () => {
    // The `current` property contains the HotTable instance
    const hot = hotRef.current.hotInstance;
    // Get the plugin from the Handsontable instance
    const exportPlugin = hot.getPlugin('exportFile');

    const blob = exportPlugin.exportAsBlob('csv', {
      bom: false,
      columnDelimiter: ',',
      columnHeaders: false,
      exportHiddenColumns: true,
      exportHiddenRows: true,
      mimeType: 'text/csv',
      rowDelimiter: '\r\n',
      rowHeaders: true
    });

    console.log(blob);
  };

  return (
    <div>
      <div>
        <button onClick={exportBlob} className="button button--primary">Export as Blob (see browser console)</button>
      </div>
      <br/>

      <HotTable
        ref={hotRef}
        data={data}
        rowHeaders={true}
        colHeaders={true}
        height="auto"
        autoWrapRow={true}
        autoWrapCol={true}
        licenseKey="non-commercial-and-evaluation"
      />
    </div>
  );
};
