TITLE: Exporting Handsontable Grid Data as String in React (TSX)
DESCRIPTION: TypeScript React implementation for exporting grid data as a string. It uses TypeScript to properly type the HotTable reference and other components, ensuring type safety.
SOURCE: https://github.com/handsontable/handsontable/blob/develop/docs/content/guides/accessories-and-menus/export-to-csv/export-to-csv.md#2025-04-10_snippet_11

LANGUAGE: tsx
CODE:
```
import { HotTable } from '@handsontable/react';
import { registerAllModules } from 'handsontable/registry';
import { useRef } from 'react';
import 'handsontable/dist/handsontable.full.min.css';

// register Handsontable's modules
registerAllModules();

export const ExampleComponent = () => {
  const hotRef = useRef<HotTable>(null);

  const data = [
    ['A1', 'B1', 'C1', 'D1'],
    ['A2', 'B2', 'C2', 'D2'],
    ['A3', 'B3', 'C3', 'D3'],
    ['A4', 'B4', 'C4', 'D4'],
  ];

  const exportString = () => {
    // The `current` property contains the HotTable instance
    const hot = hotRef.current.hotInstance;
    // Get the plugin from the Handsontable instance
    const exportPlugin = hot.getPlugin('exportFile');

    const csvContent = exportPlugin.exportAsString('csv', {
      bom: false,
      columnDelimiter: ',',
      columnHeaders: false,
      exportHiddenColumns: true,
      exportHiddenRows: true,
      rowDelimiter: '\r\n',
      rowHeaders: true
    });

    console.log(csvContent);
  };

  return (
    <div>
      <div>
        <button onClick={exportString} className="button button--primary">Export as string (see browser console)</button>
      </div>
      <br/>

      <HotTable
        ref={hotRef}
        data={data}
        rowHeaders={true}
        colHeaders={true}
        height="auto"
        autoWrapRow={true}
        autoWrapCol={true}
        licenseKey="non-commercial-and-evaluation"
      />
    </div>
  );
};
```

