TITLE: Implementing Batch Operations in React with JSX
DESCRIPTION: React implementation using JSX showing how to leverage the batch() method in Handsontable for improved performance when making multiple table modifications.
SOURCE: https://github.com/handsontable/handsontable/blob/develop/docs/content/guides/optimization/batch-operations/batch-operations.md#2025-04-10_snippet_9

LANGUAGE: jsx
CODE:
```
import { useState, useEffect, useRef } from 'react';
import { HotTable } from '@handsontable/react';
import { registerAllModules } from 'handsontable/registry';
import 'handsontable/dist/handsontable.full.min.css';

// register Handsontable's modules
registerAllModules();

export const ExampleComponent = () => {
  const hotRef = useRef(null);
  const [renderTime, setRenderTime] = useState(0);
  const [renderedData, setRenderedData] = useState(() => {
    return Array.from({ length: 300 }, (_, row) => 
      Array.from({ length: 100 }, (_, col) => `${String.fromCharCode(65 + (col % 26))}${row + 1}`)
    );
  });

  const runBatch = () => {
    const hot = hotRef.current.hotInstance;

    // create a dataset to insert
    const dataToInsert = [];
    for (let row = 0; row < 5; row++) {
      const rowData = [];
      for (let col = 0; col < 5; col++) {
        rowData.push(`${String.fromCharCode(65 + (col % 26))}${row + 1}`);
      }
      dataToInsert.push(rowData);
    }

    const t0 = performance.now();

    hot.batch(() => {
      // set the dataset at the specific coordinates
      hot.populateFromArray(5, 2, dataToInsert);

      // change the header for selected column
      hot.updateSettings({
        columns: [{
          data: 1,
          renderer(instance, td, row, col, prop, value) {
            td.innerText = `${value} USD`;
            td.style.color = '#FF0000';

            return td;
          }
        }]
      });

      // change column alignment
      hot.updateSettings({
        colWidths: [45, 100, 100, 110, 100]
      });

      // alter the table to have more rows
      hot.alter('insert_row_above', 5, 15);

      // alter the table to have more columns
      hot.alter('insert_col_start', 2, 5);
    });

    const t1 = performance.now();
    setRenderTime(Math.round(t1 - t0));
  };

  return (
    <div>
      <HotTable
        ref={hotRef}
        data={renderedData}
        width="100%"
        height={320}
        licenseKey="non-commercial-and-evaluation"
      />

      <div className="controls">
        <button onClick={runBatch}>Run</button>
        <div className="timer">Render time: <span>{renderTime}ms</span></div>
      </div>
    </div>
  );
};
```

