TITLE: Updating Auth Middleware
DESCRIPTION: Modify the auth middleware to work with Better Auth for protected routes.
SOURCE: https://github.com/haydenbleasel/next-forge/blob/main/docs/migrations/authentication/better-auth.mdx#2025-04-23_snippet_9

LANGUAGE: typescript
CODE:
```
import type { NextRequest } from "next/server";
import { NextResponse } from 'next/server';

const isProtectedRoute = (request: NextRequest) => {
  return request.url.startsWith("/dashboard"); // change this to your protected route
}

export const authMiddleware = async (request: NextRequest) => {
  const url = new URL('/api/auth/get-session', request.nextUrl.origin);
  const response = await fetch(url, {
    headers: {
      cookie: request.headers.get('cookie') || '',
    },
  });

  const session = await response.json();

  if (isProtectedRoute(request) && !session) {
    return NextResponse.redirect(new URL("/sign-in", request.url));
  }

  return NextResponse.next();
}
```

