  builder: PothosSchemaTypes.SchemaBuilder<Types>,
) {
  return builder.args((t) => ({
    limit: t.int(),
    offset: t.int(),
  }));
}

builder.queryField('getUsers', (t) =>
  t.field({
    type: [Shaveable],
    args: {
      ...createPaginationArgs(builder),
    },
    resolve: () => [],
  }),
);
```

