H.Data.prototype.options = {
    csv: {
        dateFormat: undefined,
        alternateRowColor: false,
        // Silence Highcharts errors in the CSV parsing. Not recommended to
        // set to true in production.
        silent: false,
        columnTypes: [],
        firstRowAsNames: true,
        // When using regex parsing, lines or semicolons based on this flag.
        lineDelimiter: undefined,
        // Highlight lines with error in the data, for example if the value is
        // not a number.
        highlightErrorLines: true,
        itemDelimiter: undefined,
        liveRedraw: true,
        decimalPoint: undefined,
        omitNull: undefined,
        beforeParse: undefined,
        parseDate: undefined,
        complete: undefined,
        error: undefined
    }
};
