TITLE: Consuming a Stream in a React Client Component
DESCRIPTION: This Next.js client component demonstrates how to consume a streaming response from the server. It sends a search prompt to the API endpoint, reads the stream data in chunks, and updates the UI with each piece of information received until the stream is complete.
SOURCE: https://github.com/inngest/website/blob/main/pages/docs/features/realtime/nextjs.mdx#2025-04-10_snippet_1

LANGUAGE: tsx
CODE:
```
"use client";
import { useState, useRef, useEffect } from "react";

export function SimpleSearch() {
  const [updates, setUpdates] = useState<string[]>([]);
  const [input, setInput] = useState("");

  const handleNewChat = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const response = await fetch("/api/simple-search", {
        method: "POST",
        body: JSON.stringify({ prompt: input }),
      });

      const reader = response.body?.getReader();
      if (!reader) {
        return;
      }

      while (true) {
        const { done, value } = await reader.read();
        if (done) {
          break;
        }
        const text = new TextDecoder().decode(value);
        const data = JSON.parse(text).data;
        if (data === "Search complete") {
          reader.cancel();
          break;
        } else {
          setUpdates((prev) => [...prev, data]);
        }
      }
    } catch (error) {
      console.error("Error:", error);
    } finally {
      setInput("");
    }
  };

  return (
    // ...
  );
}
```

