TITLE: Implementing a Custom Metrics Reporter for TimescaleDB
DESCRIPTION: Implementation of a custom metrics reporter that extends the tally StatsReporter interface. This code handles counter metrics by capturing the metric name, tags, and value, then recording them to TimescaleDB while adding the hostname as an additional tag.
SOURCE: https://github.com/inngest/website/blob/main/content/blog/2023-11-29-metrics-with-timescale.md#2025-04-10_snippet_1

LANGUAGE: go
CODE:
```
type metricsReporter struct {
	cachedCounters *CachedCounters
}

func (r metricsReporter) ReportCounter(name string, tags map[string]string, value int64) {
	ctx := context.Background()
	now := time.Now()

	host, err := os.Hostname()
	if err != nil {
		log.From(ctx).Err(err).Msg("failed to retrieve host value")
		return
	}

	val, err := updateCounter(r.cachedCounters, &CounterMetric{Name: name, Tags: tags, Value: value})
	if err != nil {
		log.From(ctx).
			Err(err).
			Str("metric", name).
			Int64("value", value).
			Interface("tags", tags).
			Msg(fmt.Sprintf("failed to update counter metric: %s", name))
		return
	}

	tagsWithHost := mergeTags(tags, map[string]string{"host": host})

	metric := metricspb.Counter{
		Name:  name,
		Tags:  tagsWithHost,
		Value: val,
		Ts:    timestamppb.New(now),
	}

  // recordCounter will write the data to TimescaleDB
	if err := recordCounter(ctx, &metric); err != nil {
		log.From(ctx).
			Err(err).
			Str("metric", name).
			Int64("value", value).
			Interface("tags", tags).
			Msg(fmt.Sprintf("error recording counter metric: %s", name))
	}
}

// more code...
