TITLE: Client Component with Stream Termination Handling
DESCRIPTION: This React component shows how to handle stream termination in a client component. It reads the stream data and watches for a specific 'Search complete' message that signals the end of the stream, at which point it cancels the reader and exits the read loop.
SOURCE: https://github.com/inngest/website/blob/main/pages/docs/features/realtime/nextjs.mdx#2025-04-10_snippet_4

LANGUAGE: tsx
CODE:
```
"use client";
import { useState, useRef, useEffect } from "react";

export function SimpleSearch() {
  const [updates, setUpdates] = useState<string[]>([]);
  const [input, setInput] = useState("");

  const handleNewChat = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim()) return;

    try {
      const response = await fetch("/api/simple-search", {
        method: "POST",
        body: JSON.stringify({ prompt: input }),
      });

      const reader = response.body?.getReader();
      if (!reader) {
        return;
      }

      while (true) {
        const { done, value } = await reader.read();
        if (done) {
          break;
        }
        const text = new TextDecoder().decode(value);
        const data = JSON.parse(text).data;
        if (data === "Search complete") {
          reader.cancel();
          break;
        } else {
          setUpdates((prev) => [...prev, data]);
        }
      }
    } catch (error) {
      console.error("Error:", error);
    } finally {
      setInput("");
    }
  };

  return (
    // ...
  );
}
```

