        __u64 iph_len;
        __u32 ifindex;
        __u8 protocol;
        __u32 vip_num;
        int action;

        tkey.tunnel_ttl = 64;
        if (is_ipv6) {
            __u8 buffer[sizeof(struct ipv6hdr)] = {};

            ip6h = bpf_dynptr_slice(skb_ptr, off, buffer, sizeof(buffer));
            if (!ip6h)
                return TC_ACT_SHOT;

            iph_len = sizeof(struct ipv6hdr);
            protocol = ip6h->nexthdr;
            pckt.proto = protocol;
            pkt_bytes = bpf_ntohs(ip6h->payload_len);
            off += iph_len;
            if (protocol == IPPROTO_FRAGMENT) {
                return TC_ACT_SHOT;
            } else if (protocol == IPPROTO_ICMPV6) {
                action = parse_icmpv6(skb_ptr, off, &pckt);
                if (action >= 0)
                    return action;
                off += IPV6_PLUS_ICMP_HDR;
            } else {
                memcpy(pckt.srcv6, ip6h->saddr.s6_addr32, 16);
                memcpy(pckt.dstv6, ip6h->daddr.s6_addr32, 16);
            }
        } else {
            __u8 buffer[sizeof(struct iphdr)] = {};

            iph = bpf_dynptr_slice(skb_ptr, off, buffer, sizeof(buffer));
            if (!iph || iph->ihl != 5)
                return TC_ACT_SHOT;

            protocol = iph->protocol;
            pckt.proto = protocol;
            pkt_bytes = bpf_ntohs(iph->tot_len);
            off += IPV4_HDR_LEN_NO_OPT;

            if (iph->frag_off & PCKT_FRAGMENTED)
                return TC_ACT_SHOT;
            if (protocol == IPPROTO_ICMP) {
                action = parse_icmp(skb_ptr, off, &pckt);
                if (action >= 0)
                    return action;
                off += IPV4_PLUS_ICMP_HDR;
            } else {
                pckt.src = iph->saddr;
                pckt.dst = iph->daddr;
            }
        }
        protocol = pckt.proto;

        if (protocol == IPPROTO_TCP) {
            if (!parse_tcp(skb_ptr, off, &pckt))
                return TC_ACT_SHOT;
        } else if (protocol == IPPROTO_UDP) {
            if (!parse_udp(skb_ptr, off, &pckt))
                return TC_ACT_SHOT;
        } else {
            return TC_ACT_SHOT;
        }
