public interface IDocumentSessionListener
{
    // Called *before* calling SaveChanges/SaveChangesAsync
    void BeforeSaveChanges(IDocumentSession session);

    // Called *after* a successful SaveChanges/SaveChangesAsync
    // invocation
    void AfterCommit(IDocumentSession session);

    // Called *after* a failed SaveChanges/SaveChangesAsync
    // invocation
    void AfterRollback(IDocumentSession session, Exception ex);

    // Called *before* a document is inserted
    void DocumentInserted<T>(IDocumentSession session, T document);

    // Called *before* a document is updated
    void DocumentUpdated<T>(IDocumentSession session, T document);

    // Called *before* a document is deleted
    void DocumentDeleted<T>(IDocumentSession session, T document);

    // Called *before* a document is soft-deleted
    void DocumentSoftDeleted<T>(IDocumentSession session, T document);
}
