public class IssuesByTitleWithAssigneesDictionary : ICompiledListQuery<Issue, IssuesByTitleWithAssigneesDictionary>
{
    public string TitleSearch { get; set; }

    public Dictionary<Guid, User> Included { get; set; } = new Dictionary<Guid, User>();

    public Expression<Func<IQueryable<Issue>, IEnumerable<IssuesByTitleWithAssigneesDictionary>>> QueryIs()
    {
        return query => query
            .Include(x => x.AssigneeId, x => Included)
            .Where(x => x.Title.Contains(TitleSearch))
            .AsEnumerable();
    }
}
```

