    <groupHeader>
      <band height="25">
        ...
        <element kind="textField" ... bookmarkLevel="1">
          <expression><![CDATA[$V{FirstLetter}]] ></expression>
          <anchorNameExpression><![CDATA["Letter " + $V{FirstLetter}]] ></anchorNameExpression>
        </element>
      </band>
