@Setter @Getter
@Entity
@Table
public class Course {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;
    private String name;

    public static Course newCourse(String name) {
        var course = new Course();
        course.setName(name);
        return course;
    }

}
