    "email": "string",
    "phone_number": "string",
    "state_code": "string",
    "residential": false,
    "street_number": "string",
    "address_line1": "string",
    "address_line2": "string",
    "validate_location": false
  },
  "options": {},
  "reference": "string",
  "shipment_identifiers": [
    "string"
  ],
  "metadata": {},
  "manifest_url": "string",
  "messages": [
    {
      "message": "string",
      "code": "string",
      "details": {},
