  --header 'Authorization: Token API_KEY' \
  --header 'Content-Type: application/json' \
  --data '{
    "shipment": {
        "label_type": "PDF",
        "reference": "my-reference",
        "payment": {
            "paid_by": "sender",
            "currency": "CAD",
            "account_number": "2343253"  
        },
        "options": {
