  collapsible={false}
  name={"country_code"}
  required={true}
  schemaName={"string"}
  qualifierMessage={"**Possible values:** [`AC`, `AD`, `AE`, ...]"}
  schema={{"enum":["AC","AD","AE",...],"type":"string","x-spec-enum-id":"05a7b91de773db3e","description":"The address country code"}}
>
