  partial_shipment_update(input: $data) {
    shipment {
      id
      status
      recipient {
        id
        postal_code
        city
        person_name
        company_name
        country_code
        email
        phone_number
        state_code
        suburb
        residential
        street_number
        address_line1
        address_line2
        federal_tax_id
        state_tax_id
        validate_location
      }
      shipper {
        id
        postal_code
        city
        person_name
        company_name
        country_code
        email
        phone_number
        state_code
        suburb
        residential
        street_number
        address_line1
        address_line2
        federal_tax_id
        state_tax_id
        validate_location
      }
      billing_address {
        id
        postal_code
        city
        person_name
        company_name
        country_code
        email
        phone_number
        state_code
        suburb
        residential
        street_number
        address_line1
        address_line2
        federal_tax_id
        state_tax_id
        validate_location
      }
      parcels {
        id
        width
        height
        length
        is_document
        dimension_unit
        weight
        weight_unit
        packaging_type
        package_preset
        freight_class
        reference_number
        items {
          id
          weight
          title
          description
          quantity
          sku
          hs_code
          value_amount
          weight_unit
          value_currency
          origin_country
          metadata
          parent_id
        }
      }
      label_type
      service
      reference
      customs {
        id
        certify
        commercial_invoice
        content_type
        content_description
        incoterm
        invoice
        invoice_date
        signer
        duty {
          paid_by
          currency
          account_number
          declared_value
        }
        options
        commodities {
          id
          weight
          weight_unit
          title
          description
          quantity
          sku
          hs_code
          value_amount
          value_currency
          origin_country
          metadata
          parent_id
        }
        duty_billing_address {
          id
