```
function Toggle() {
	const [on, setOn] = React.useState(false)
	const toggle = () => setOn((o) => !o)
	return <Switch on={on} onToggle={toggle} />
}

function Switch({ on, onToggle }) {
	return (
		<div>
			<SwitchMessage on={on} />
			<SwitchButton onToggle={onToggle} />
		</div>
	)
}

