		this._name = name
		this.greeting = 'Hey there!'
	}
	setName(strName) {
		this._name = strName
	}
	getName() {
		return this._getPrefixedName('Name')
	}
	getGreetingCallback() {
		const { greeting, _name } = this
		return (subject) => `${greeting} ${subject}, I'm ${_name}`
