TITLE: Configuring Twitter API Client in TypeScript
DESCRIPTION: This snippet defines a TwitterApi class that handles interactions with the Twitter API. It includes configuration for API endpoints, bearer token management, and methods for fetching tweet data.
SOURCE: https://github.com/kentcdodds/kentcdodds.com/blob/main/app/utils/twitter/README.md#2025-04-12_snippet_0

LANGUAGE: TypeScript
CODE:
```
import { TweetData } from './api-types'

const API_ENDPOINT = 'https://api.twitter.com'
const SYNDICATION_URL = 'https://syndication.twitter.com'

function getToken() {
  return process.env.TWITTER_BEARER_TOKEN
}

export class TwitterApi {
  private static _instance: TwitterApi
  private token: string | null = null

  private constructor() {}

  public static get instance(): TwitterApi {
    return this._instance || (this._instance = new TwitterApi())
  }

  private async getToken() {
    if (!this.token) {
      this.token = getToken()
    }
    return this.token
  }

  async getTweet(id: string): Promise<TweetData> {
    const requestUrl = `${API_ENDPOINT}/2/tweets/${id}?expansions=author_id,attachments.media_keys,referenced_tweets.id,referenced_tweets.id.author_id,entities.mentions.username,attachments.poll_ids&media.fields=url,alt_text,type,width,height,variants&poll.fields=options,end_datetime,duration_minutes&tweet.fields=created_at,public_metrics,entities,source,text&user.fields=profile_image_url,verified,name,username`
    const res = await fetch(requestUrl, {
      headers: {
        authorization: `Bearer ${await this.getToken()}`,
      },
    })

    if (res.ok) {
      const data = await res.json()
      return data
    }

    if (res.status === 404) {
      return this.getTweetFromScraper(id)
    }

    throw new Error(`Failed to fetch tweet data: ${res.statusText}`)
  }

  private async getTweetFromScraper(id: string): Promise<TweetData> {
    const requestUrl = `${SYNDICATION_URL}/tweet-result?id=${id}`
    const res = await fetch(requestUrl)

    if (!res.ok) {
      throw new Error(`Failed to fetch tweet data: ${res.statusText}`)
    }

    return res.json()
  }
}
