TITLE: Creating Dynamic Post Pages with Next.js and Keystone
DESCRIPTION: Implements dynamic page generation for individual blog posts using getStaticPaths and getStaticProps. Handles post content retrieval and display with navigation.
SOURCE: https://github.com/keystonejs/keystone/blob/main/docs/content/blog/embedded-mode-with-sqlite-nextjs.md#2025-04-13_snippet_8

LANGUAGE: tsx
CODE:
```
// pages/post/[slug].tsx

import { GetStaticPathsResult, GetStaticPropsContext, InferGetStaticPropsType } from 'next';
import Link from 'next/link';

import { query } from '.keystone/api';
import type { Lists } from '.keystone/types';

type Post = {
  id: string;
  title: string;
  content: string;
};

export default function PostPage({ post }: { post: Post }) {
  return (
    <div>
      <main style={{ margin: '3rem' }}>
        <div>
          <Link href="/">
            &larr; back home
          </Link>
        </div>
        <h1>{post.title}</h1>
        <p>{post.content}</p>
      </main>
    </div>
  );
}

export async function getStaticPaths(): Promise<GetStaticPathsResult> {
  const posts = (await query.Post.findMany({
    query: `slug`,
  })) as { slug: string }[];

  const paths = posts.filter(({ slug }) => !!slug).map(({ slug }) => `/post/${slug}`);

  return {
    paths,
    fallback: false,
  };
}

export async function getStaticProps({ params }: GetStaticPropsContext) {
  const post = (await query.Post.findOne({
    where: { slug: params!.slug as string },
    query: 'id title content',
  })) as Post | null;
  if (!post) {
    return { notFound: true };
  }
  return { props: { post } };
}
```

