TITLE: Configuring CronJob Conversion Webhook in Go
DESCRIPTION: This code snippet sets up a conversion webhook for the CronJob custom resource. It defines methods for converting between different versions of the CronJob object and registers the webhook with the API server.
SOURCE: https://github.com/kubernetes-sigs/kubebuilder/blob/master/docs/book/src/multiversion-tutorial/webhooks.md#2025-04-21_snippet_0

LANGUAGE: Go
CODE:
```
package v1

import (
	"sigs.k8s.io/controller-runtime/pkg/conversion"

	batchv1 "tutorial.kubebuilder.io/project/api/v1"
	batchv2 "tutorial.kubebuilder.io/project/api/v2"
)

// ConvertTo converts this CronJob to the Hub version (v2).
func (src *CronJob) ConvertTo(dstRaw conversion.Hub) error {
	dst := dstRaw.(*batchv2.CronJob)

	// ObjectMeta
	dst.ObjectMeta = src.ObjectMeta

	// Spec
	dst.Spec.Schedule = src.Spec.Schedule
	dst.Spec.StartingDeadlineSeconds = src.Spec.StartingDeadlineSeconds
	dst.Spec.ConcurrencyPolicy = batchv2.ConcurrencyPolicy(src.Spec.ConcurrencyPolicy)
	dst.Spec.Suspend = src.Spec.Suspend
	dst.Spec.JobTemplate = src.Spec.JobTemplate
	dst.Spec.SuccessfulJobsHistoryLimit = src.Spec.SuccessfulJobsHistoryLimit
	dst.Spec.FailedJobsHistoryLimit = src.Spec.FailedJobsHistoryLimit

	if src.Spec.TimeZone != nil {
		dst.Spec.TimeZone = src.Spec.TimeZone
	}

	// Status
	dst.Status.LastScheduleTime = src.Status.LastScheduleTime
	dst.Status.LastSuccessfulTime = src.Status.LastSuccessfulTime
	for _, srcActive := range src.Status.Active {
		dst.Status.Active = append(dst.Status.Active, srcActive)
	}

	return nil
}

// ConvertFrom converts from the Hub version (v2) to this version.
func (dst *CronJob) ConvertFrom(srcRaw conversion.Hub) error {
	src := srcRaw.(*batchv2.CronJob)

	// ObjectMeta
	dst.ObjectMeta = src.ObjectMeta

	// Spec
	dst.Spec.Schedule = src.Spec.Schedule
	dst.Spec.StartingDeadlineSeconds = src.Spec.StartingDeadlineSeconds
	dst.Spec.ConcurrencyPolicy = batchv1.ConcurrencyPolicy(src.Spec.ConcurrencyPolicy)
	dst.Spec.Suspend = src.Spec.Suspend
	dst.Spec.JobTemplate = src.Spec.JobTemplate
	dst.Spec.SuccessfulJobsHistoryLimit = src.Spec.SuccessfulJobsHistoryLimit
	dst.Spec.FailedJobsHistoryLimit = src.Spec.FailedJobsHistoryLimit

	if src.Spec.TimeZone != nil {
		dst.Spec.TimeZone = src.Spec.TimeZone
	}

	// Status
	dst.Status.LastScheduleTime = src.Status.LastScheduleTime
	dst.Status.LastSuccessfulTime = src.Status.LastSuccessfulTime
	for _, srcActive := range src.Status.Active {
		dst.Status.Active = append(dst.Status.Active, srcActive)
	}

	return nil
}
