var jsondata  = []byte(`{"foo": [{"first": "a", "last": "b"},
                           {"first": "c", "last": "d"}]}`)
var data interface{}
err := json.Unmarshal(jsondata, &data)
result, err := jmespath.search({"foo[*].first", data)
result [ 'a', 'c' ]

var jsondata = []byte(`{"foo": [{"age": 20}, {"age": 25},
                           {"age": 30}, {"age": 35},
                           {"age": 40}]}`)
