		Long userId, Long receiverUserId, String title, String body,
		JSONObject payloadJSONObject, int notificationType,
		ServiceContext serviceContext, Map<String, Serializable> options)
		throws PortalException {

		// Add message to database

		BigDecimal amount = new BigDecimal(
			payloadJSONObject.getString("amount"));

		DateTime dueDate = new DateTime(
			payloadJSONObject.getLong("dueDate"));

		_callService(
			receiver, abc, payloadJSONObject.getString("reference"),
			amount, dueDate, new ServiceContext());
	}

	@Override
	protected boolean isDeliver(
		UserNotificationDeliveryType userNotificationDeliveryType) {

		return true;
	}

	@Override
	protected void processMessage(
		UserNotificationEvent userNotificationEvent, long userId,
		long receiverUserId, Map<String, Serializable> options)
		throws PortalException {

		JSONObject notificationEventJSONObject =
			JSONFactoryUtil.createJSONObject(
				userNotificationEvent.getPayload());

		long classPK = notificationEventJSONObject.getLong("classPK");

		if (classPK == 0) {
			return;
		}

		// Process the message
	}

	@Reference
	private ABCLocalService _abcLocalService;

}
