package com.liferay.portal.kernel.notifications.internal;

import com.liferay.osgi.service.tracker.collections.ServiceTrackerCollections;
import com.liferay.osgi.service.tracker.collections.ServiceTrackerList;
import com.liferay.osgi.service.tracker.collections.ServiceTrackerMap;
import com.liferay.osgi.service.tracker.collections.ServiceTrackerMapFactory;
import com.liferay.portal.kernel.notifications.NotificationHandler;
import com.liferay.portal.kernel.notifications.NotificationInteractor;
import com.liferay.portal.kernel.notifications.NotificationInteractorFactory;
import com.liferay.portal.kernel.notifications.UserNotificationDefinition;
import com.liferay.portal.kernel.notifications.UserNotificationFeedEntry;
import com.liferay.portal.kernel.notifications.UserNotificationHandler;
import com.liferay.portal.kernel.notifications.UserNotificationInteractor;
import com.liferay.portal.kernel.service.ServiceReference;

import java.util.Collection;

import org.osgi.framework.BundleContext;
import org.osgi.service.component.annotations.Activate;
import org.osgi.service.component.annotations.Component;
import org.osgi.service.component.annotations.Deactivate;

/**
 * @author Adolfo Prez
 */
@Component(immediate = true, service = NotificationInteractorFactory.class)
public class NotificationInteractorFactoryImpl
	implements NotificationInteractorFactory {

	@Activate
	public void activate(BundleContext bundleContext) {
		_notificationHandlerServiceTrackerList =
			ServiceTrackerCollections.openList(
				NotificationHandler.class, bundleContext);

		_userNotificationHandlersServiceTrackerMap =
			ServiceTrackerMapFactory.openSingleValueMap(
				bundleContext, UserNotificationHandler.class, null,
				(serviceReference, emitter) -> {
					UserNotificationHandler userNotificationHandler =
						bundleContext.getService(serviceReference);

					emitter.emit(userNotificationHandler.getPortletId());
				});
	}

	@Override
	public NotificationInteractor createNotificationInteractor() {
		return new NotificationInteractorImpl(
			_notificationHandlerServiceTrackerList);
	}

	@Override
	public UserNotificationInteractor createUserNotificationInteractor() {
		return new UserNotificationInteractorImpl(
			_userNotificationHandlersServiceTrackerMap);
	}

	@Deactivate
	public void deactivate() {
		_notificationHandlerServiceTrackerList.close();
		_userNotificationHandlersServiceTrackerMap.close();
	}

	private ServiceTrackerList<NotificationHandler> _notificationHandlerServiceTrackerList;
	private ServiceTrackerMap<String, UserNotificationHandler> _userNotificationHandlersServiceTrackerMap;

	private static class NotificationInteractorImpl
		implements NotificationInteractor {

		public NotificationInteractorImpl(
			ServiceTrackerList<NotificationHandler> notificationHandlerServiceTrackerList) {

			_notificationHandlerServiceTrackerList =
				notificationHandlerServiceTrackerList;
		}

		@Override
		public void deliver(String portletId, long[] userIds, String payload) {
			Collection<NotificationHandler> notificationHandlers =
				_notificationHandlerServiceTrackerList.getServices();

			for (NotificationHandler notificationHandler : notificationHandlers) {
				notificationHandler.deliver(portletId, userIds, payload);
			}
		}

		@Override
		public void flush() {
			Collection<NotificationHandler> notificationHandlers =
				_notificationHandlerServiceTrackerList.getServices();

			for (NotificationHandler notificationHandler : notificationHandlers) {
				notificationHandler.flush();
			}
		}

		private final ServiceTrackerList<NotificationHandler> _notificationHandlerServiceTrackerList;

	}

	private static class UserNotificationInteractorImpl
		implements UserNotificationInteractor {

		public UserNotificationInteractorImpl(
			ServiceTrackerMap<String, UserNotificationHandler> userNotificationHandlersServiceTrackerMap) {

			_userNotificationHandlersServiceTrackerMap =
				userNotificationHandlersServiceTrackerMap;
		}

		@Override
		public void applyTo(
			UserNotificationFeedEntry userNotificationFeedEntry) {

			String portletId = userNotificationFeedEntry.getPortletId();

			UserNotificationHandler userNotificationHandler =
				_userNotificationHandlersServiceTrackerMap.getService(portletId);

			if (userNotificationHandler != null) {
				userNotificationHandler.interpret(
					userNotificationFeedEntry,
					UserNotificationDefinition.NOTIFICATION_TYPE_ACTIONABLE, true);
			}
		}

		private final ServiceTrackerMap<String, UserNotificationHandler>
			_userNotificationHandlersServiceTrackerMap;

	}

}
```

