TITLE: Browser-based Session Signatures
DESCRIPTION: Implementation of session signature generation in a browser environment using Web3Provider.
SOURCE: https://github.com/lit-protocol/docs/blob/main/docs/sdk/access-control/quick-start.md#2025-04-18_snippet_11

LANGUAGE: jsx
CODE:
```
import { ethers } from "ethers";
import { LIT_ABILITY } from "@lit-protocol/constants";
import {
  LitAccessControlConditionResource,
  createSiweMessageWithRecaps,
  generateAuthSig,
} from "@lit-protocol/auth-helpers";

class Lit {
  ...

  async getSessionSignatures(){
     // Connect to the wallet
     const provider = new ethers.providers.Web3Provider(window.ethereum);
     await provider.send("eth_requestAccounts", []);
     const signer = provider.getSigner();
     const walletAddress = await signer.getAddress();
     console.log("Connected account:", walletAddress);

     // Get the latest blockhash
     const latestBlockhash = await this.litNodeClient.getLatestBlockhash();

     // Define the authNeededCallback function
     const authNeededCallback = async(params) => {
       if (!params.uri) {
         throw new Error("uri is required");
       }
       if (!params.expiration) {
         throw new Error("expiration is required");
       }

       if (!params.resourceAbilityRequests) {
         throw new Error("resourceAbilityRequests is required");
       }

       // Create the SIWE message
       const toSign = await createSiweMessageWithRecaps({
         uri: params.uri,
         expiration: params.expiration,
         resources: params.resourceAbilityRequests,
         walletAddress: walletAddress,
         nonce: latestBlockhash,
         litNodeClient: this.litNodeClient,
       });

       // Generate the authSig
       const authSig = await generateAuthSig({
         signer: signer,
         toSign,
       });

       return authSig;
     }

     // Define the Lit resource
     const litResource = new LitAccessControlConditionResource('*');

     // Get the session signatures
     const sessionSigs = await this.litNodeClient.getSessionSigs({
         chain: this.chain,
         resourceAbilityRequests: [
             {
                 resource: litResource,
                 ability: LIT_ABILITY.AccessControlConditionDecryption,
             },
         ],
         authNeededCallback,
         capacityDelegationAuthSig,
     });
     return sessionSigs;
  }

  ...
}
