    authNeededCallback,
  });

  const pkpWallet = new PKPEthersWallet({
    pkpPubKey: PKP_PUBLIC_KEY,
    controllerSessionSigs: sessionSigs,
    controllerAuthMethods: [],
  });

  await pkpWallet.init();

