getReplies(): Promise<CommentThread> {
  if (!this.has_replies || !this.comment?.key('comment').key('comment_id').string())
    throw new Error('Cannot retrieve replies for this thread, they do not exist.');

  if (!this.comment_replies_data)
    throw new Error('Cannot retrieve replies for this thread without required data.');

  return this.comment_replies_data.getContinuation();
}
