get filters(): string[] {
  return this.memo.getType('filters', () => {
    const filter_chips = this.page.contents?.twoColumnBrowseResultsRenderer?.tabs?.find((tab) => tab.tabRenderer?.selected)?.tabRenderer?.content?.sectionListRenderer?.subMenu?.channelSubMenuRenderer?.filterChipBar?.filterChipBarRenderer?.chips;
    return filter_chips?.map((chip) => chip.filterChipRenderer?.label?.simpleText) || [];
  });
}

get content_type_filters(): string[] {
  return this.memo.getType('content_type_filters', () => {
    const filter_chips = this.page.contents?.twoColumnBrowseResultsRenderer?.tabs?.find((tab) => tab.tabRenderer?.selected)?.tabRenderer?.content?.sectionListRenderer?.subMenu?.channelSubMenuRenderer?.contentTypeSubMenuItems;
    return filter_chips?.map((chip) => chip.title) || [];
  });
}
```

