            ...$pages,
            // This is just a standard Filament page
            // see https://filamentphp.com/docs/3.x/panels/pages#creating-a-page
            'my-page-route-name' => MyPage::route('/{record}/my-page'),
        ];
    }

    public function extendSubNavigation(array $nav) : array
    {
        return [
            ...$nav,
            // This is just a standard Filament page
            // see https://filamentphp.com/docs/3.x/panels/pages#creating-a-page
            MyPage::class,
        ];
    }
}

