    curl_setopt($ch, CURLOPT_URL, 'https://api.mailgun.net/v4/domains/AUTHORITY_DOMAIN_NAME/keys/SELECTOR/activate');
    $result = curl_exec($ch);
    curl_close($ch);

    return $result;
  }
