import (
    "context"
    "github.com/mailgun/mailgun-go/v3"
    "time"
)

func ChangePassword(domain, apiKey string) error {
    mg := mailgun.NewMailgun(domain, apiKey)

    ctx, cancel := context.WithTimeout(context.Background(), time.Second*30)
