    };
  },
});

// ã‚¹ãƒ†ãƒƒãƒ—2: ã‚¹ãƒ†ãƒƒãƒ—1ã‹ã‚‰ã®ãƒ‡ãƒ¼ã‚¿ã‚’å‡¦ç†
const processData = new Step({
  id: "processData",
  inputSchema: z.object({
    previousValue: z.string(),
  }),
  execute: async ({ context }) => {
    // previousValueã¯å¤‰æ•°ãƒžãƒƒãƒ”ãƒ³ã‚°ã®ãŸã‚ã«åˆ©ç”¨å¯èƒ½ã«ãªã‚Šã¾ã™
    const { previousValue } = context.inputData;

    return {
      result: `Processed: ${previousValue}`
    };
  },
});

// ãƒ¯ãƒ¼ã‚¯ãƒ•ãƒ­ãƒ¼ã‚’ä½œæˆ
const workflow = new Workflow({
  name: "step-mapping",
});

// ã‚¹ãƒ†ãƒƒãƒ—1ã‹ã‚‰ã‚¹ãƒ†ãƒƒãƒ—2ã¸ã®ãƒ‡ãƒ¼ã‚¿ã‚’ãƒžãƒƒãƒ”ãƒ³ã‚°
workflow
  .step(generateData)
  .then(processData, {
    variables: {
      // generateDataã®å‡ºåŠ›ã‹ã‚‰nested.valueãƒ—ãƒ­ãƒ‘ãƒ†ã‚£ã‚’ãƒžãƒƒãƒ”ãƒ³ã‚°
