TITLE: Fetching Weather Data using Open Meteo API
DESCRIPTION: Defines a step to fetch weather forecast data for a given city using the Open Meteo API. It uses geocoding to find the city's coordinates and then retrieves the weather forecast.
SOURCE: https://github.com/mastra-ai/mastra/blob/main/docs/src/content/ja/examples/agents/agentic-workflows.mdx#_snippet_1

LANGUAGE: typescript
CODE:
```
import { Step } from "@mastra/core/workflows";
import { z } from "zod";

const fetchWeather = new Step({
  id: "fetch-weather",
  description: "æŒ‡å®šã•ã‚ŒãŸéƒ½å¸‚ã®å¤©æ°—äºˆå ±ã‚’å–å¾—ã—ã¾ã™",
  inputSchema: z.object({
    city: z.string().describe("å¤©æ°—ã‚’å–å¾—ã™ã‚‹éƒ½å¸‚"),
  }),
  execute: async ({ context }) => {
    const triggerData = context?.getStepResult<{ city: string }>("trigger");

    if (!triggerData) {
      throw new Error("ãƒˆãƒªã‚¬ãƒ¼ãƒ‡ãƒ¼ã‚¿ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“");
    }

    const geocodingUrl = `https://geocoding-api.open-meteo.com/v1/search?name=${encodeURIComponent(triggerData.city)}&count=1`;
    const geocodingResponse = await fetch(geocodingUrl);
    const geocodingData = await geocodingResponse.json();

    if (!geocodingData.results?.[0]) {
      throw new Error(`å ´æ‰€ '${triggerData.city}' ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“`);
    }

    const { latitude, longitude, name } = geocodingData.results[0];

    const weatherUrl = `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&daily=temperature_2m_max,temperature_2m_min,precipitation_probability_mean,weathercode&timezone=auto`;
    const response = await fetch(weatherUrl);
    const data = await response.json();

    const forecast = data.daily.time.map((date: string, index: number) => ({
      date,
      maxTemp: data.daily.temperature_2m_max[index],
      minTemp: data.daily.temperature_2m_min[index],
      precipitationChance: data.daily.precipitation_probability_mean[index],
      condition: getWeatherCondition(data.daily.weathercode[index]),
      location: name,
    }));

    return forecast;
  },
});
