```
<?php\n\ndeclare(strict_types=1);\n\nnamespace Mautic\UserBundle\Entity;\n\nuse Doctrine\ORM\Mapping as ORM;\nuse Ramsey\Uuid\Uuid;\n\n/**\n * @ORM\Table (name=\"worlds\")\n */\nclass World\n{\n    /**\n     * @ORM\Column(type=\"guid\")\n     * @ORM\Id\n     */\n    private $id;\n
```

