'mautic.campaign.model.campaign' => [
    'class'     => \Mautic\CampaignBundle\Model\CampaignModel::class,
    'arguments' => [
        'mautic.lead.model.list',
        'mautic.form.model.form',
        'mautic.campaign.event_collector',
