TITLE: HL7 to FHIR Bot Implementation (TypeScript)
DESCRIPTION: This TypeScript code defines a Medplum Bot that receives HL7 messages, parses them, and creates corresponding FHIR Patient resources. It handles HL7 ADT messages, extracts patient information, and ensures that a patient with the same MRN is not created twice.
SOURCE: https://github.com/medplum/medplum/blob/main/packages/docs/docs/bots/hl7-into-fhir.mdx#_snippet_0

LANGUAGE: ts
CODE:
```
import {
  HL7Message,
  MedplumClient,
  createReference,
  normalizeErrorString,
  Reference,
  ResourceType,
} from '@medplum/core';
import { Patient } from '@medplum/fhirtypes';

/**
 * Medplum Bot to process HL7 ADT messages and create FHIR Patient resources.
 * @param medplum - Medplum client.
 * @param event - Event data from the Medplum server.
 * @returns - An ACK message to the EHR.
 */
export async function handler(medplum: MedplumClient, event: any): Promise<any> {
  const hl7MessageText = event.input;
  try {
    const hl7 = new HL7Message(hl7MessageText);

    // Extract data from the HL7 message
    const mrnNumber = hl7.get('PID', 3, 1);
    const firstName = hl7.get('PID', 5, 1);
    const lastName = hl7.get('PID', 5, 0);
    const birthDate = hl7.get('PID', 7);
    const gender = hl7.get('PID', 8);

    // Check if the patient already exists
    const patientSearchResult = await medplum.search('Patient', {
      identifier: `mrn|${mrnNumber}`,
    });

    if (patientSearchResult.entry?.length) {
      const patient = patientSearchResult.entry[0].resource as Patient;
      console.log(`Patient already exists: ${patient.id}`);

      // Acknowledge the message
      return `MSH|^~\\&|CL|PDMT|Primary||${new Date().toISOString()}||ACK|${Date.now()}|P|2.5.1\rMSA|AA|${
        hl7.messageControlId
      }|OK`;
    }

    // Create a new patient
    const patient: Patient = {
      resourceType: 'Patient',
      identifier: [
        {
          type: {
            coding: [
              {
                system: 'http://terminology.hl7.org/CodeSystem/v2-0203',
                code: 'MR',
              },
            ],
            text: 'Medical Record Number',
          },
          value: mrnNumber,
        },
      ],
      name: [
        {
          family: lastName,
          given: [firstName],
        },
      ],
      birthDate,
      gender,
    };

    const outcome = await medplum.create(patient);

    console.log(`Created patient: ${outcome.id}`);

    // Acknowledge the message
    return `MSH|^~\\&|CL|PDMT|Primary||${new Date().toISOString()}||ACK|${Date.now()}|P|2.5.1\rMSA|AA|${
      hl7.messageControlId
    }|OK`;
  } catch (err) {
    console.log('HL7 processing error', err, hl7MessageText);
    return `MSH|^~\\&|CL|PDMT|Primary||${new Date().toISOString()}||ACK|${Date.now()}|P|2.5.1\rMSA|AE|${
      event.messageControlId
    }|${normalizeErrorString(err)}`;
  }
}

```

