TITLE: HTTP File Upload Bot TypeScript
DESCRIPTION: This snippet shows the complete example bot to create and upload a PDF to an HTTP endpoint.
It imports necessary modules, creates a PDF using `medplum.createPdf`, downloads the PDF data, constructs a FormData object, and posts the form to a specified HTTP endpoint.
SOURCE: https://github.com/medplum/medplum/blob/main/packages/docs/docs/bots/file-uploads.mdx#_snippet_4

LANGUAGE: typescript
CODE:
```
import { MedplumClient, MedplumConfig, BotEvent, createReference, Binary } from '@medplum/core';
import { randomUUID } from 'crypto';
import FormData from 'form-data';
import fetch from 'node-fetch';

// Helper function to pause the bot and release resources
function nl(): Promise<void> {
  return new Promise((resolve) => {
    setTimeout(resolve, 0);
  });
}

// Helper function to pause the bot and release resources
function closeFn(): Promise<void> {
  return new Promise((resolve) => {
    setTimeout(resolve, 0);
  });
}

/**
 * Medplum bot to upload to an HTTP endpoint
 * @param medplum - medplum client
 * @param event - the BotEvent
 * @returns
 */
export async function handler(medplum: MedplumClient, event: BotEvent<any>): Promise<void> {
  const pdfId = await createPdf(medplum);
  await checkBinary(medplum, pdfId);
  const binaryData = await downloadPdf(medplum, pdfId);
  await nl();
  const form = await formData(binaryData);
  await nl();
  const result = await postForm(form);
  console.log(result);
  await closeFn();

  return;
}

async function createPdf(medplum: MedplumClient): Promise<string> {
  const binary = await medplum.createPdf({
    resourceType: 'DocumentReference',
    status: 'current',
    content: [
      {
        attachment: {
          contentType: 'application/pdf',
        },
      },
    ],
  });
  return binary.id as string;
}

async function checkBinary(medplum: MedplumClient, binaryId: string): Promise<void> {
  const binary = await medplum.readResource<Binary>('Binary/' + binaryId);
  if (!binary) {
    throw new Error('Binary not found');
  }
}

async function downloadPdf(medplum: MedplumClient, binaryId: string): Promise<Buffer> {
  const binary = await medplum.readBinary(binaryId);
  return Buffer.from(binary.data as string, 'base64');
}

async function formData(binaryData: Buffer): Promise<FormData> {
  const form = new FormData();
  form.append('file', binaryData, 'test.pdf');
  return form;
}

async function postForm(form: FormData): Promise<any> {
  const response = await fetch('https://httpbin.org/post', {
    method: 'POST',
    body: form as any,
    headers: form.getHeaders(),
  });
  return response.json();
}

```

