import { createReference } from '@medplum/core';
import { Practitioner, Patient, Observation, Resource, RiskAssessment, List } from '@medplum/fhirtypes';

export function dupedPatientAssessment(patient1: Patient, patient2: Patient): RiskAssessment {
  return {
    resourceType: 'RiskAssessment',
    status: 'completed',
    code: {
      coding: [
        {
          system: 'https://medplum.com/fhir/CodeSystem/duplicate-record',
          code: 'duplicate-patient',
          display: 'Duplicate Patient'
        }
      ],
      text: 'Duplicate Patient'
    },
    subject: createReference(patient1),
    basis: [createReference(patient2)],
    occurrenceDateTime: new Date().toISOString(),
    method: 'email - phone-number - last-name - gender - dob',
    probabilityDecimal: 85.0,
    qualitativeRisk: 'Very likely'
  }
}

export function doNotMatch(patient: Patient, patientsNotToMatch: Patient[]): List {
  return {
    resourceType: 'List',
    status: 'current',
    mode: 'working',
    code: {
      coding: [
        {
          system: 'https://medplum.com/fhir/CodeSystem/do-not-match',
          code: 'do-not-match',
          display: 'Do Not Match'
        }
      ],
      text: 'Do Not Match'
    },
    subject: createReference(patient),
    entry: patientsNotToMatch.map(p => ({
      item: createReference(p)
    }))
  }
}

