media = {
  'resourceType': 'Media',
  'basedOn': [{
    'reference': 'ServiceRequest/12345'
  }],
  'status': 'completed',    # `status` is a required field
  'content': {
    'title': 'plums-python.jpg',
    'contentType': 'image/jpeg',
    'url': MEDIA_URL,
  }
};

