  <button>This is a header</button>
</div>

{/* as semantic heading */}
<AccordionHeader as="h4">This is a header</AccordionHeader>
{/* Generated html */}
<h4>
  <button>This is a header</button>
</h4>

{/* if no semantic heading can be used */}
