    input: NonNullable<Slot<'input'>> & {
        orient?: 'horizontal' | 'vertical';
    };
};

// @public (undocumented)
export type SliderState = ComponentState<SliderSlots> & Pick<SliderProps, 'disabled' | 'size' | 'vertical'>;

// @public (undocumented)
