  return <Button>Hello world!</Button>;
}

function App() {
  return (
    <>
      {/* ðŸŸ¢ works as Button handles classes properly */}
      <Fade>
        <Button />
      </Fade>
      {/* ðŸ”´ fails as MyButton does not handle classes properly */}
      <Fade>
        <MyButton />
